// frontend/src/components/GameDesignForm.js
import React, { useState } from 'react';
import '../styles/GameDesignForm.css';

function GameDesignForm({ onSuccess, onError }) {
  const [formData, setFormData] = useState({
    project_name: '',
    genre: 'RTS',
    match_duration: '15-20 minutes',
    skill_ceiling: 'high',
    audience: 'competitive gamers',
    platform: 'PC/Mobile',
    version: '1.0.0',
    tenant_id: 'default-tenant',
    constraints: {
      notes: ''
    }
  });

  const [uploadedFile, setUploadedFile] = useState(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [progress, setProgress] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === 'notes') {
      setFormData(prev => ({
        ...prev,
        constraints: {
          ...prev.constraints,
          notes: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleFileChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    // Validate file type
    const validTypes = [
      'text/plain', 
      'application/pdf', 
      'application/vnd.openxmlformats-officedocument.presentationml.presentation', // .pptx
      'application/vnd.ms-powerpoint' // .ppt (older format)
    ];
    const validExtensions = ['.txt', '.pdf', '.pptx', '.ppt'];
    const fileExtension = '.' + file.name.split('.').pop().toLowerCase();

    if (!validTypes.includes(file.type) && !validExtensions.includes(fileExtension)) {
      alert('Please upload a .txt, .pdf, .ppt, or .pptx file');
      return;
    }

    // Validate file size (max 5MB)
    if (file.size > 5 * 1024 * 1024) {
      alert('File size must be less than 5MB');
      return;
    }

    // Convert file to base64
    const reader = new FileReader();
    reader.onload = () => {
      const base64Content = reader.result.split(',')[1];
      setUploadedFile({
        name: file.name,
        type: file.type,
        content: base64Content
      });
      setProgress(`File "${file.name}" ready to upload`);
    };
    reader.readAsDataURL(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setProgress('Initializing workflow...');

    try {
      // Prepare the payload
      const payload = {
        ...formData
      };

      // Add uploaded file if present
      if (uploadedFile) {
        payload.uploaded_file = uploadedFile;
      }

      console.log('Submitting to n8n:', payload);
      setProgress('Sending to n8n workflow...');

      // Replace with your actual n8n webhook URL
      const n8nWebhookUrl = process.env.REACT_APP_N8N_WEBHOOK_URL || 
        'http://localhost:5678/webhook/gamedesign-pipeline/start';

      const response = await fetch(n8nWebhookUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload)
      });

      if (!response.ok) {
        throw new Error(`Workflow failed: ${response.statusText}`);
      }

      const result = await response.json();

      setProgress('Design generated successfully! âœ…');

      console.log('n8n workflow result:', result);

      // Show success message
      if (onSuccess) {
        onSuccess(result);
      }

      // Reset form
      setTimeout(() => {
        setFormData({
          project_name: '',
          genre: 'RTS',
          match_duration: '15-20 minutes',
          skill_ceiling: 'high',
          audience: 'competitive gamers',
          platform: 'PC/Mobile',
          version: '1.0.0',
          tenant_id: 'default-tenant',
          constraints: { notes: '' }
        });
        setUploadedFile(null);
        setProgress('');
      }, 2000);

    } catch (error) {
      console.error('Error submitting form:', error);
      setProgress('');
      if (onError) {
        onError(error.message);
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="game-design-form">
      <div className="form-header">
        <h2>ðŸŽ® Create New Game Design</h2>
        <p>Generate a complete game design document using AI</p>
      </div>

      <form onSubmit={handleSubmit}>
        <div className="form-section">
          <h3>Basic Information</h3>

          <div className="form-group">
            <label htmlFor="project_name">
              Project Name <span className="required">*</span>
            </label>
            <input
              type="text"
              id="project_name"
              name="project_name"
              value={formData.project_name}
              onChange={handleChange}
              placeholder="e.g., Cyber Clash, Space Warriors"
              required
            />
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="genre">Genre</label>
              <select
                id="genre"
                name="genre"
                value={formData.genre}
                onChange={handleChange}
              >
                <option value="RTS">Real-Time Strategy (RTS)</option>
                <option value="Turn-based Strategy">Turn-based Strategy</option>
                <option value="4X Strategy">4X Strategy</option>
                <option value="MOBA">MOBA</option>
                <option value="Tower Defense">Tower Defense</option>
                <option value="Auto-battler">Auto-battler</option>
                <option value="Card Battler">Card Battler</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="platform">Platform</label>
              <select
                id="platform"
                name="platform"
                value={formData.platform}
                onChange={handleChange}
              >
                <option value="PC/Mobile">PC/Mobile</option>
                <option value="PC">PC Only</option>
                <option value="Mobile">Mobile Only</option>
                <option value="Console">Console</option>
                <option value="Web Browser">Web Browser</option>
              </select>
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="match_duration">Match Duration</label>
              <select
                id="match_duration"
                name="match_duration"
                value={formData.match_duration}
                onChange={handleChange}
              >
                <option value="5-10 minutes">5-10 minutes</option>
                <option value="10-15 minutes">10-15 minutes</option>
                <option value="15-20 minutes">15-20 minutes</option>
                <option value="20-30 minutes">20-30 minutes</option>
                <option value="30-45 minutes">30-45 minutes</option>
                <option value="45+ minutes">45+ minutes</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="skill_ceiling">Skill Ceiling</label>
              <select
                id="skill_ceiling"
                name="skill_ceiling"
                value={formData.skill_ceiling}
                onChange={handleChange}
              >
                <option value="low">Low (Casual)</option>
                <option value="medium">Medium (Intermediate)</option>
                <option value="high">High (Competitive)</option>
                <option value="very high">Very High (Esports)</option>
              </select>
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="audience">Target Audience</label>
            <select
              id="audience"
              name="audience"
              value={formData.audience}
              onChange={handleChange}
            >
              <option value="casual gamers">Casual Gamers</option>
              <option value="competitive gamers">Competitive Gamers</option>
              <option value="strategy enthusiasts">Strategy Enthusiasts</option>
              <option value="mobile gamers">Mobile Gamers</option>
              <option value="hardcore gamers">Hardcore Gamers</option>
              <option value="family audience">Family Audience</option>
            </select>
          </div>
        </div>

        <div className="form-section">
          <h3>Additional Details (Optional)</h3>

          <div className="form-group">
            <label htmlFor="notes">
              Design Notes & Requirements
            </label>
            <textarea
              id="notes"
              name="notes"
              value={formData.constraints.notes}
              onChange={handleChange}
              placeholder="Describe your vision, special mechanics, themes, or specific requirements..."
              rows="4"
            />
            <small>
              Provide any additional context that will help create a better design
            </small>
          </div>

          <div className="form-group">
            <label htmlFor="file">
              Upload Design Document
            </label>
            <div className="file-upload-area">
              <input
                type="file"
                id="file"
                accept=".txt,.pdf,.ppt,.pptx"
                onChange={handleFileChange}
              />
              <div className="file-upload-label">
                {uploadedFile ? (
                  <div className="file-uploaded">
                    <span>ðŸ“„ {uploadedFile.name}</span>
                    <button
                      type="button"
                      onClick={() => {
                        setUploadedFile(null);
                        setProgress('');
                      }}
                      className="remove-file"
                    >
                      Remove
                    </button>
                  </div>
                ) : (
                  <div className="file-upload-placeholder">
                    <span>ðŸ“Ž Click to upload .txt, .pdf, or .pptx</span>
                    <small>Max size: 5MB</small>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>

        {progress && (
          <div className={`progress-message ${isSubmitting ? 'submitting' : 'success'}`}>
            {progress}
          </div>
        )}

        <div className="form-actions">
          <button
            type="submit"
            disabled={isSubmitting || !formData.project_name}
            className="submit-button"
          >
            {isSubmitting ? (
              <>
                <span className="spinner-small"></span>
                Generating Design...
              </>
            ) : (
              <>
                ðŸš€ Generate Game Design
              </>
            )}
          </button>
        </div>
      </form>
    </div>
  );
}

export default GameDesignForm;
